class TennisGame {
  player1 = {
    name: "Player 1",
    score: 0
  };

  player2 = {
    name: "Player 2",
    score: 0
  };

  constructor(playerOneName, playerTwoName) {
    this.player1.name = playerOneName;
    this.player2.name = playerTwoName;

    this.matchPossibleScore = ["Love", "Fifteen", "Thirty", "Forty", "Won"];
    this.anyoneHasWon = false;
  }

  get name1() {
    return this.player1.name;
  }

  get name2() {
    return this.player2.name;
  }

  get score1() {
    return this.player1.score;
  }

  get score2() {
    return this.player2.score;
  }

  get win1() {
    return this.player1.win;
  }

  get win2() {
    return this.player2.win;
  }

  tie = (player1, player2) => {
    return player1.score === player2.score;
  };

  tieAt40 = (playerWon, playerLost) => {
    return (
      playerWon.score >= 3 &&
      playerLost.score >= 3 &&
      playerLost.score === playerWon.score
    );
  };

  hasAdvantage = (playerOne, playerTwo) =>
    playerOne.score === playerTwo.score + 1 && playerOne.score > 3;

  hasWon = (playerOne, playerTwo) =>
    playerOne.score >= playerTwo.score + 2 && playerOne.score > 3;

  wonPoint = playerName => {
    if (this.player1.name === playerName) {
      this.player1.score++;
    }

    if (this.player2.name === playerName) {
      this.player2.score++;
    }
  };

  getScore = () => {
    let score = "";
    let actionDone = false;

    if (this.tieAt40(this.player1, this.player2) && !actionDone) {
      score = "Deuce";
      actionDone = true;
    }

    if (this.tie(this.player1, this.player2) && !actionDone) {
      score = `${this.matchPossibleScore[this.player1.score]}-All`;
      actionDone = true;
    }

    if (this.hasWon(this.player1, this.player2) && !actionDone) {
      score = `Win for ${this.name1}`;
      this.anyoneHasWon = true;
      actionDone = true;
    }

    if (this.hasWon(this.player2, this.player1) && !actionDone) {
      score = `Win for ${this.name2}`;
      this.anyoneHasWon = true;
      actionDone = true;
    }

    if (this.hasAdvantage(this.player1, this.player2) && !actionDone) {
      score = `Advantage ${this.name1}`;
      actionDone = true;
    }

    if (this.hasAdvantage(this.player2, this.player1) && !actionDone) {
      score = `Advantage ${this.name2}`;
      actionDone = true;
    }

    if (!actionDone) {
      score = `${this.matchPossibleScore[this.score1]}-${
        this.matchPossibleScore[this.score2]
      }`;
      actionDone = true;
    }

    return score;
  };
}

export default TennisGame;
